sub addCountIndivLibTask {
#....................................................................................................................................................#
#	subroutineCategory: unassigned
#	dependOnSub: >none
#	appearInSub: >none
#	primaryAppearInSection: 1_run|123
#	secondaryAppearInSection: >none
#	input: $path_hsh_ref, $run_outDir, $run_tag, $task_info_hsh_ref
#	output: 
#	toCall: &addCountIndivLibTask($path_hsh_ref, $task_info_hsh_ref, $run_tag, $run_outDir);
#	calledInLine: 128
#....................................................................................................................................................#
	my ($path_hsh_ref, $task_info_hsh_ref, $run_tag, $run_outDir) = @_;
	
	my $task_num = keys %{$task_info_hsh_ref};
	open CTSSLIST, "<", $path_hsh_ref->{'lib_list_path'};
	while (<CTSSLIST>) {
		chomp;
			my ($libID, $long_ctss_bed_path, $collapse_ctss_bed_path) = split /\t/;
		my $outputPrefix = $libID;
		my $task_outDir = "$run_outDir/count/";
		$task_num++;
		$task_info_hsh_ref->{$task_num} = {
			'task_name' => 'count',
			'script_name' => 'tool.bk.count.pl',
			'param' => [
				['countRegion_bed_path', $path_hsh_ref->{"tCRE_bed_path"}],
				['ctss_bed_path', $collapse_ctss_bed_path],
				['outputPrefix', $outputPrefix],
				['outDir', $run_outDir."/count"],
			],
			'check' => [
				$run_outDir."/count/$outputPrefix/log/count.log.tsv",
			]
		};
	}
	close CTSSLIST;

	return ();
}
sub currentTime {
#....................................................................................................................................................#
#	subroutineCategory: general
#	dependOnSub: >none
#	appearInSub: prepDir|381
#	primaryAppearInSection: >none
#	secondaryAppearInSection: 0_startTasks|112
#	input: none
#	output: $runTime
#	toCall: my ($runTime) = &currentTime();
#	calledInLine: 395
#....................................................................................................................................................#
	
	my ($sec,$min,$hour,$mday,$mon,$year,$wday,$yday,$isdst)=localtime(time);
	my $runTime = sprintf "%04d-%02d-%02d %02d:%02d", $year+1900, $mon+1,$mday,$hour,$min;	
	
	return $runTime;
}
sub define_path {
