	foreach my $level (qw/robust lenient default/) {
		$path_hsh_ref->{"$level.filtered_tssCluster_bed_path"} = $run_outDir."/filter/".$run_tag."/bed/".$run_tag.".tssCluster.".$level.".filtered.bed.gz";
	}

	return ($path_hsh_ref);

}
